contracts/MouchDrops.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract MouchDrops {
	address public owner;
	uint256 public cooldownPeriod = 10 minutes;
	uint256 public maxDisbursement = 30 ether;
	mapping(address => uint256) public lastRequestTime; // Track last request time per address

	event EtherReceived(address indexed sender, uint256 amount);
	event EtherDisbursed(address indexed recipient, uint256 amount);
	event CooldownPeriodUpdated(uint256 newCooldownPeriod);
	event MaxDisbursementUpdated(uint256 newMaxDisbursement);
	event OwnerWithdraw(address indexed owner, uint256 amount);

	modifier onlyOwner() {
		require(msg.sender == owner, "Caller is not the owner");
		_;
	}

	constructor() {
		owner = msg.sender;
	}

	receive() external payable {
		emit EtherReceived(msg.sender, msg.value);
	}

	function disburse(
		address payable recipient,
		uint256 amount,
		uint256 nonce,
		bytes memory signature
	) external {
		require(recipient != address(0), "Invalid recipient");
		require(amount > 0 && amount <= maxDisbursement, "Invalid amount");
		require(
			address(this).balance >= amount,
			"Insufficient contract balance"
		);
		require(
			block.timestamp >= lastRequestTime[msg.sender] + cooldownPeriod,
			"Cooldown period not expired"
		);

		// ✅ Verify the ECDSA signature
		bytes32 messageHash = keccak256(
			abi.encodePacked(recipient, amount, nonce)
		);
		require(
			_isValidSignature(messageHash, signature),
			"Invalid game signature"
		);

		// ✅ Prevent replay attacks
		lastRequestTime[msg.sender] = block.timestamp;

		// ✅ Transfer the reward
		(bool sent, ) = recipient.call{ value: amount }("");
		require(sent, "Failed to disburse rewards");

		emit EtherDisbursed(recipient, amount);
	}

	function _isValidSignature(
		bytes32 hash,
		bytes memory signature
	) internal view returns (bool) {
		bytes32 ethSignedMessageHash = _getEthSignedMessageHash(hash);
		address recoveredSigner = _recoverSigner(
			ethSignedMessageHash,
			signature
		);

		// ✅ Debugging
		require(
			recoveredSigner == owner,
			string(
				abi.encodePacked(
					"Invalid signature: Expected ",
					toAsciiString(owner),
					" but got ",
					toAsciiString(recoveredSigner)
				)
			)
		);

		return recoveredSigner == owner;
	}

	// ✅ Helper function to convert address to readable string
	function toAsciiString(address x) internal pure returns (string memory) {
		bytes memory s = new bytes(42); // 2 for '0x' + 40 for address
		s[0] = "0";
		s[1] = "x";
		for (uint i = 0; i < 20; i++) {
			bytes1 b = bytes1(uint8(uint160(x) / (2 ** (8 * (19 - i)))));
			s[2 * i + 2] = _char(bytes1(uint8(b) / 16));
			s[2 * i + 3] = _char(bytes1(uint8(b) % 16));
		}
		return string(s);
	}

	function _char(bytes1 b) private pure returns (bytes1 c) {
		return b < 0x0a ? bytes1(uint8(b) + 0x30) : bytes1(uint8(b) + 0x57);
	}

	function _getEthSignedMessageHash(
		bytes32 hash
	) private pure returns (bytes32) {
		return
			keccak256(
				abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)
			);
	}

	function _recoverSigner(
		bytes32 ethSignedMessageHash,
		bytes memory signature
	) private pure returns (address) {
		(bytes32 r, bytes32 s, uint8 v) = _splitSignature(signature);
		return ecrecover(ethSignedMessageHash, v, r, s);
	}

	function _splitSignature(
		bytes memory sig
	) private pure returns (bytes32 r, bytes32 s, uint8 v) {
		require(sig.length == 65, "Invalid signature length");
		assembly {
			r := mload(add(sig, 32))
			s := mload(add(sig, 64))
			v := byte(0, mload(add(sig, 96)))
		}
	}

	function setCooldownPeriod(uint256 newCooldownPeriod) external onlyOwner {
		cooldownPeriod = newCooldownPeriod;
		emit CooldownPeriodUpdated(newCooldownPeriod);
	}

	function setMaxDisbursement(uint256 newMaxDisbursement) external onlyOwner {
		require(
			newMaxDisbursement > 0,
			"Max disbursement must be greater than zero"
		);
		maxDisbursement = newMaxDisbursement;
		emit MaxDisbursementUpdated(newMaxDisbursement);
	}

	function withdrawAll() external onlyOwner {
		uint256 contractBalance = address(this).balance;
		require(contractBalance > 0, "No DMON to withdraw");

		(bool sent, ) = owner.call{ value: contractBalance }("");
		require(sent, "Failed to withdraw DMON");

		emit OwnerWithdraw(owner, contractBalance);
	}

	function transferOwnership(address newOwner) external onlyOwner {
		require(newOwner != address(0), "New owner cannot be zero address");
		owner = newOwner;
	}
}
